import React, { useState } from 'react';
import { connect } from 'react-redux';
import { useNavigate } from 'react-router';
import 'react-toastify/dist/ReactToastify.css';


import image from '../../../assets/signup.svg';
import { signup } from '../../../redux/actions/auth';
import { fetch_ngos } from '../../../redux/actions/ngos.jsx';
import picture from '../../../assets/bnr.svg';
import SignupSnipper from './SpinnerBtn';
import {
    SignupBackground,
    Logo,
    SignupContainer,
    Content,
    Header,
    Picture,
    LogoHeader,
    Small,
    Title,
    FormContainer,
    MyForm,
    ButtonContainer,
    StyledLink,
    Label,
    Form,
    InputField,
    InputFieldSelect,
    InputOption,
    Paragraph,
} from './signupStyle.js';


const SignUp = ({ signup, isAuthenticated, fetch_ngos, ngos }) => {
    const navigate = useNavigate();

    const [loading, setLoading] = useState(false);
   

    const format = '^(?=.*[0-9])(?=.*[a-zA-Z])(?=.*[!@#$%^&*])[a-zA-Z0-9!@#$%^&*]{8,20}$';
    const navigateTo = (path = '/') => navigate(path, { replace: true });

    const onSubmit = (e) => {
        e.preventDefault();

        if (!loading) {
            setLoading(false);
            if (password.length < 8) {
                toast.warn('Password should be at least 8 characters ', {
                    position: 'top-right',
                    autoClose: 4000,
                });
            } else if (password.search(/[a-z]/) < 0) {
                toast.warn(
                    'Your password must contain at least one lower case letter(example q, f, a). ',
                    {
                        position: 'top-right',
                        autoClose: 4000,
                    },
                );
            } else if (password.search(/[A-Z]/) < 0) {
                toast.warn(
                    'Your password must contain at least one UPPER CASE letter(example A, T, B). ',
                    {
                        position: 'top-right',
                        autoClose: 4000,
                    },
                );
            } else if (password.search(/[0-9]/) < 0) {
                toast.warn('Your password must contain at least one DIGIT(example 1, 2, 4). ', {
                    position: 'top-right',
                    autoClose: 4000,
                });
            } else if (password.search(/[!@#\$%\^&\*_]/) < 0) {
                toast.warn(
                    'Your password must contain at least one special character example -[ ! @ # $ % ^ & * _ ]',
                    {
                        position: 'top-right',
                        autoClose: 4000,
                    },
                );
            } else if (password !== confirmPassword) {
                toast.warn('Passwords do not match ', {
                    position: 'top-right',
                    autoClose: 4000,
                });
            } else {
                signup(
                    ngoName,
                    ngoType,
                    fullName,
                    position,
                    email,
                    password,
                    setLoading,
                    navigateTo,
                );
            }
        }
    };

    const onChange = (e) => {
        setFormValues({ ...formValues, [e.target.name]: e.target.value });
    };
    const handleSelectChange = (data) => {
        setFormValues({ ...formValues, ngoName: data['value'] });
        setSelectValue(data['label']);
    };

    if (isAuthenticated) {
        navigateTo();
    }

    const handleFocus = (_) => {
        setFocused(true);
    };

    const getNGOs = async (_) => {
        await fetch_ngos('ngo');

        let options = [];
        ngos.forEach((ngo) => {
            if (ngo['name'] !== 'Admin') {
                options.push({ value: ngo['id'], label: ngo['name'] });
            }
        });
        return options;
    };

    return (
        <SignupBackground>
            <Logo src={image} />
            <SignupContainer>
                <Content>
                    <Header>
                        <Picture src={picture} />
                        <LogoHeader>
                            NBR <Small>SCGs Data Map</Small>
                        </LogoHeader>
                    </Header>
                </Content>
                <FormContainer>
                    <Title>NGO Sign Up</Title>
                    <MyForm onSubmit={(e) => onSubmit(e)} autoComplete="false">
                        <Form>
                            <Label>NGO name</Label>
                            <AsyncSelect
                                key={1}
                                cacheOptions
                                defaultOptions
                                classname="js-example-basic-single"
                                className="inputs"
                                loadOptions={getNGOs}
                                placeholder="Select Your Organisation"
                                value={selectValue}
                                inputValue={selectValue}
                                onChange={handleSelectChange}
                                isSearchable
                                name="ngoName"
                                isMulti={false}
                            />
                        </Form>
                        <Form>
                            <Label>Full name</Label>
                            <InputField
                                id="3"
                                key={3}
                                name="fullName"
                                type="text"
                                placeholder="input name"
                                errorMessage="Your name should be 5-25 characters and should not include any special character"
                                label="Full name"
                                required={true}
                                onChange={onChange}
                                onBlur={handleFocus}
                                className="inputs"
                                focused={focused.toString()}
                            />
                        </Form>
                        <Form>
                            <Label>Position</Label>
                            <InputField
                                id="4"
                                key={4}
                                name="position"
                                type="text"
                                placeholder="input position"
                                errorMessage="Please input your position"
                                required={true}
                                onChange={onChange}
                                onBlur={handleFocus}
                                className="inputs"
                                focused={focused.toString()}
                            />
                        </Form>
                        <Form>
                            <Label>Email</Label>
                            <InputField
                                id="5"
                                key={5}
                                name="email"
                                type="email"
                                placeholder="input email"
                                errorMessage="Please input a valid email"
                                required={true}
                                onChange={onChange}
                                onBlur={handleFocus}
                                focused={focused.toString()}
                            />
                        </Form>
                        <Form>
                            <Label>Password</Label>
                            <InputField
                                id="6"
                                key={6}
                                name="password"
                                type="password"
                                placeholder="input password"
                                errorMessage="Please input your password"
                                required={true}
                                onChange={onChange}
                                onBlur={handleFocus}
                                className="inputs"
                                focused={focused.toString()}
                                // pattern={format}
                            />
                        </Form>
                        <Form>
                            <Label>Confirm Password</Label>
                            <InputField
                                id="7"
                                key={7}
                                name="confirmPassword"
                                type="password"
                                placeholder="re-type your password"
                                errorMessage="Please enter a strong password"
                                required={true}
                                onChange={onChange}
                                onBlur={handleFocus}
                                className="inputs"
                                focused={focused.toString()}
                                // pattern={format}
                            />
                        </Form>
                        <ButtonContainer>
                            <SignupSnipper
                                loading={loading}
                                type="submit"
                                title="Submit sign up request"
                            />
                            <StyledLink to="/login">
                                <Paragraph>Login</Paragraph>
                            </StyledLink>
                        </ButtonContainer>
                    </MyForm>
                </FormContainer>
            </SignupContainer>
        </SignupBackground>
    );
};

const mapStateToProps = (state) => ({
    isAuthenticated: state.auth.isAuthenticated,
    ngos: state.ngos.ngos,
});

export default connect(mapStateToProps, { signup, fetch_ngos })(SignUp);
